import { Injectable, EventEmitter } from '@angular/core';
import { Recipe } from './recipe.model';
import { Ingreditent } from '../shared/ingredient.model';
import { ShoppingListService } from '../shopping-list/shopping-list.service';

@Injectable({
  providedIn: 'root',
})
export class RecipeService {
  recipeSelected = new EventEmitter<Recipe>();

  private recipes: Recipe[] = [
    new Recipe(
      'Com Suon',
      'Com suon 50 ngan',
      'data:image/jpeg;base64,',
      [
        new Ingreditent('Meat', 1),
        new Ingreditent('Meat', 2),
        new Ingreditent('Meat', 3),
        new Ingreditent('Meat', 4),
        new Ingreditent('Meat', 5),
      ]
    ),
    new Recipe(
      'Com Suon',
      'Com suon 50 ngan',
      'data:image/jpeg;base64,',
      []
    ),
    new Recipe(
      'Com Suon',
      'Com suon 50 ngan',
      'data:image/jpeg;base64,',
      []
    ),
    new Recipe(
      'Com Suon',
      'Com suon 50 ngan',
      'data:image/jpeg;base64,',
      []
    ),
  ];

  constructor(private shoppingListService: ShoppingListService) {}

  getRecipes() {
    return this.recipes.slice();
  }

  addIngredientToShopping(ingredients: Ingreditent[]){
    this.shoppingListService.addIngredients(ingredients);
  }

}
